import React from 'react';
import { Button, Input } from 'antd';
// import 'antd/dist/antd.css';
import { w3cwebsocket as W3CWebSocket } from 'websocket';

const { TextArea } = Input;

const client = new W3CWebSocket('ws://43.128.42.40:8088/websocket');


const DisplayBox = () => {
  const handlePostClick = () => {
    // 处理点击“Post”按钮的逻辑
    // 这里可以添加你想要的逻辑
    client.onopen = () => {
      console.log('WebSocket Client Connected');
    };
    client.onmessage = (message) => {
      const newData = JSON.parse(message.data);
      setData(newData);
    };

  };

  return (
    <div style={{ padding: '20px' }}>
      <Button type="primary" onClick={handlePostClick}>
        Post
      </Button>
      <div style={{ marginTop: '20px' }}>
        <TextArea rows={8} placeholder="大显示框 1" readOnly={true} />
      </div>
      <div style={{ marginTop: '20px' }}>
        <TextArea rows={8} placeholder="大显示框 2" readOnly={true} />
      </div>
    </div>
  );
};

export default DisplayBox;
